import React, { useState } from "react";
import ProgressBar from "./qiz/progress-bar";
import * as questions from "./questions.json";

function App() {
  console.log((questions.toString()));
  const [questionNumber, setQuestionNumber] = useState(1);
  return (
    <div>
      <ProgressBar width="10vw" />
      <div>
        <h1>Question 16 of 20</h1>
        <p>{questions[0].category}</p>

        <p>{questions[0].question}</p>
        <div>
          {makeOptions(
            questions[0].correct_answer,
            questions[0].incorrect_answers
          ).map((item) => (
            <button key={item} type="submit">
              {(item)}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
}

function makeOptions(correctOption, incorrectOptions) {
  return shuffle([...incorrectOptions, correctOption]);
}

function shuffle(array) {
  var currentIndex = array.length,
    temporaryValue,
    randomIndex;

  while (0 !== currentIndex) {
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex -= 1;

    temporaryValue = array[currentIndex];
    array[currentIndex] = array[randomIndex];
    array[randomIndex] = temporaryValue;
  }

  return array;
}

export default App;
