import React, { useState } from "react";
import ProgressBar from "./qiz/progress-bar";
import questions from "./questions.json";
import "./App.css";
import Difficulty from "./qiz/difficulty";

function App() {
  const [i, setI] = useState(0);
  const [showResult, setShowResult] = useState(false);

  const onOptionSelect = (value) => {
    console.log(value);
  }

  return (
    <div>
      <ProgressBar width="10vw" />
      <div className="center">
        <h1>
          Question {i+1} of {questions.length}
        </h1>
        <p>{unescape(questions[0].category)}</p>

        <Difficulty level={questions[0].difficulty} />

        <p>{unescape(questions[0].question)}</p>
        <div className="options">
          {makeOptions(
            questions[0].correct_answer,
            questions[0].incorrect_answers
          ).map((item) => (
            <div key={item} className="answer-button-div">
              <button className="answer-button" onClick={() => onOptionSelect(item)}>
                {unescape(item)}
              </button>
            </div>
          ))}
        </div>
        {showResult && <div className="result">
          <h2>Correct</h2>
          <button
            className="answer-button"
            style={{ width: "fit-content" }}
            type="submit"
          >
            Next Question
          </button>
        </div>}
      </div>
    </div>
  );
}

function makeOptions(correctOption, incorrectOptions) {
  return shuffle([...incorrectOptions, correctOption]);
}

function shuffle(array) {
  var currentIndex = array.length,
    temporaryValue,
    randomIndex;

  while (0 !== currentIndex) {
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex -= 1;

    temporaryValue = array[currentIndex];
    array[currentIndex] = array[randomIndex];
    array[randomIndex] = temporaryValue;
  }

  return array;
}

export default App;
